class ImpEffectInputCommand(sublime_plugin.TextCommand):
    def input(self, args):
        if 'effect' not in args:
            return ImpEffectInputHandler()
    def run(self, edit, effect):
        if effect == "All Effects":
            sublime.run_command("imperator_effect_log")
        else:
            tab_name = effect
            input, effect_header = MatchInput(effect)
            _selected_effect = """
                <h2>{effect_header}</h2>
                <p>{input}</p>
                               """.format(effect_header=effect_header, input=input)

            window = sublime.active_window()
            view = window.active_view()
            window.new_html_sheet(tab_name, '', flags=0, group=- 1)
            html_sheet = window.active_sheet()
            html_sheet.set_contents(_selected_effects.format(css=css, _selected_effect=_selected_effect))

    def input_description(self):
        return "Effect Type"


